import React from 'react'
import {loadStripe} from '@stripe/stripe-js';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {useCart,useDispatchCart} from '../components/ContextReducer'
import { json } from 'react-router-dom';


 const Cart= ()=> {
  const Data = useCart();
  // console.log(Data);
  const dispatch = useDispatchCart();

  if (Data.length === 0) {
    return (
      <div>
        <div className='m-5 w-100 text-center fs-3'>The Cart is Empty!</div>
      </div>
    )
  }


  const handleRemove = (index)=>{
    dispatch({type:"REMOVE",index:index})
  }





  const handleCheckOut = async()=>{
    const stripe = await loadStripe('pk_test_51Nw1jxSCI1FYJohVnri9jjzHQgASJOVXXa67MNwPrWL9XhhBptUEbE0PNUBy5vvKpvOw7xdZcS9mICnDmjxN5GA400QDpgKXcz');

     const header ={
      "Content-Type": "application/json"
     }
     const response = await fetch("http://localhost:5000/api/create-checkout-session",{
         method:"POST",
         headers:header,
         body:json.stringify({Name:Data.name, Price:Data.price, Qty:Data.qty, })
     });

     const session = await response.json();
     const result = stripe.redirectToCheckout({
      sessionId:session.id
     });

     if(result.error){
      console.log(result.error)
     }
  }

  // useEffect(()=>{
  //   handleCheckOut();
  // },[])

   let totalPrice = Data.reduce((total, item) => total + item.price, 0)

  return (
    <div>
      <div className='container m-auto mt-5 table-responsive  table-responsive-sm table-responsive-md' >
        <table className='table table-hover '>
          <thead className=' text-success fs-4'>
            <tr>
              <th scope='col' >#</th>
              <th scope='col' >Name</th>
              <th scope='col' >Quantity</th>
              <th scope='col' >Option</th>
              <th scope='col' >Amount</th>
              <th scope='col' >Delete</th>
            </tr>
          </thead>
          <tbody>


            { 
              Data.map((item, index) => (
              <tr>
                <th scope='row' >{index + 1}</th>
                <td >{item.name}</td>
                <td>{item.qty}</td>
                <td>{item.size}</td>
                <td>{item.price}</td>
                <td ><button type="button" className="btn p-0" onClick={handleRemove}>delete</button> </td>

                </tr>
            ))}
          </tbody>
        </table> 
        <div><h1 className='fs-2'>Total Price: {totalPrice}/-</h1></div>
        <div>
          <button className='btn bg-success mt-5 ' onClick={()=>handleCheckOut()}> Payment</button>
        </div>
      </div>
    </div>
  )
}

export default Cart





