import React,{useState} from 'react'
import { Link ,useNavigate} from 'react-router-dom';
import Badge from "react-bootstrap/Badge"
import Modal from '../Modal';
import Cart from '../screen/FoodCart';
import {useCart} from './ContextReducer'


const Nav = function Navbar() {
  let data = useCart();

  const [cartView,setCartView]= useState(true);
  const Navigate= useNavigate();
  function handle(){
    localStorage.removeItem('authtoken');
     Navigate('/');
  }


  return (
    <div>

      <nav className='bg-white flex'>

      <div className='text-5xl antialiased italic font-serif font-semibold mt-2 ml-6'>
         <Link className="no-underline text-blue-600" to="/">Prabs</Link>
        </div>
        <div className='container-fluid  justify-end'>
         <ul className='flex justify-end py-3 space-x-5 mt-2 mr-5'>
           <li> 
              <Link className="no-underline  text-xl font-semibold" to="/"> 
               Home
              </Link>
            </li>

            <li>
            {(localStorage.getItem('authtoken')) ? <div className=''>
                <Link className="no-underline text-black text-xl font-semibold " to="/myorder"> Orders</Link>
              </div> : ''} 
            </li>

             <li>
             {(!localStorage.getItem('authtoken')) ? <div className='space-x-5'>
              <Link className="no-underline text-black text-xl font-semibold" to='/login'>Login</Link>
              <Link className="no-underline text-black text-xl font-semibold" to='/signup'>SignUp</Link>
            </div>
             : <div className='space-x-5'>

              <button className="text-black text-xl font-semibold" onClick={()=>{ setCartView(false) }}>
                  Cart <Badge pill bg='danger'>{data.length===0?'':data.length}</Badge> 
              </button>
              {cartView ? null : <Modal onclose={()=>{setCartView(true)}}><Cart/></Modal>} 
              <button className="text-black text-xl font-semibold" onClick={handle}>LogOut</button>
              </div> 

              }
             </li>
         </ul>
        </div>

      </nav>

    </div>
  )
}
export default Nav;






