import React,{useState} from 'react'
import { Link ,useNavigate} from 'react-router-dom';
import Badge from "react-bootstrap/Badge"
import Modal from '../Modal';
import Cart from '../screen/FoodCart';
import {useCart} from './ContextReducer'


const Nav = function Navbar() {
  let data = useCart();

  const [cartView,setCartView]= useState(true);
  const Navigate= useNavigate();
  function handle(){
    localStorage.removeItem('authtoken');
     Navigate('/');
  }


  return (
    <div>
      <nav className='bg-orange-400 mt-px-2 '>
        <div className='container-fluid flex'>

        <div className='text-5xl antialiased italic font-serif font-semibold'>
         <Link className="no-underline text-white" to="/">Prabs</Link>
        </div>

         <ul className='flex py-3 space-x-5 justify-end'>
           <li> 
              <Link className="no-underline text-white text-xl font-semibold" aria-current="page" to="/"> 
               Home
              </Link>
            </li>
            <li>
            {(localStorage.getItem('authtoken')) ? <div className=''>
                <Link className="no-underline text-white text-xl font-semibold " aria-current="page" to="/myorder"> Orders</Link>
              </div> : ''} 
            </li>

             <li>
             {(!localStorage.getItem('authtoken')) ? <div className='navbar-nav'>
            <div className='flex-row-reverse '>
              <Link className="btn bg-white text-success mx-1" to='/login'>Login</Link>
              <Link className="btn bg-white text-success mx-1" to='/signup'>SignUp</Link>
            </div>
              </div> : <div>

              <button className="btn bg-white text-success mx-1" onClick={()=>{ setCartView(false) }}>
                  Cart <Badge pill bg='danger'>{data.length===0?'':data.length}</Badge> 
              </button>
              {cartView ? null : <Modal onclose={()=>{setCartView(true)}}><Cart/></Modal>} 
              <button className="btn bg-white text-success mx-1" onClick={handle}>LogOut</button>
              </div> 

              }
             </li>
         </ul>
        </div>

      </nav>

    </div>
  )
}
export default Nav;
